package bitmap.transformer;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

class AppTest {
    @Test void appHasAGreeting() {
        App classUnderTest = new App();
        assertNotNull(classUnderTest.getGreeting(), "app should have a greeting");
    }
    String inputFilePath = "./src/main/resources/wallpaper.bmp";
    String outputFilePath = "./src/main/resources/";
    String purpleFileName = "NewCoffeePurple.bmp";
    Bitmap testFile = new Bitmap(inputFilePath,outputFilePath, purpleFileName);

    @Test
    public void testReadFile() {
        assertEquals( true, testFile.readFile(),"File not found");
    }

    @Test
    public void testSaveFile() {
        testFile.readFile();
        assertEquals( true, testFile.saveFile() ,"File not saved");
    }

    @Test
    public void testPurpleIze() {
        testFile.readFile();
        assertEquals( -4553837, testFile.purpleIze() ,"Return: RGB(48, 29, 87)");
    }

    @Test
    public void testImageFlipHorizontal() {
        testFile.readFile();
        int lastRGBVal = -16119286;
        assertEquals( lastRGBVal, testFile.imageFlipHorizontal(),"File not flipped horizontally");
    }

    @Test
    public void testImageFlipVertical() {
        testFile.readFile();
        int lastRGBVal = -15528437;
        assertEquals( lastRGBVal, testFile.imageFlipVertical(),"File not flipped vertically");
    }
}

